import  {Card} from 'react-bootstrap'
import classes from './Meal.module.css'
const Meal=props=>{
 const recipe=props.mealData
//  const ing=recipe[`strIngredient${10}`]
const array=[{
  key:"1",
  recipe:""
}]
 for (let i=1; i<21 ;i++){
   if (recipe[`strIngredient${i}`]){
     array.push({
       key:Math.random(),
       recipe:recipe[`strIngredient${i}`],
      })
     }
   }
 
 
 
 return (
  <Card style={{ width: '18rem' }}>
  <Card.Body>
    <Card.Title>{recipe.strMeal}</Card.Title>
    <img className={classes.img} src={recipe.strMealThumb} alt="Biryani.jpg" />
     {array.map(item=>(
       <p key={item.key}>{item.recipe}</p>
     ))}
    <Card.Subtitle className="mb-2 text-muted">#{recipe.strMeal}</Card.Subtitle>
    <Card.Text>
    {recipe.strInstructions}
    </Card.Text>
    <Card.Link href="#">Watch on YouTube</Card.Link>
    <Card.Link href="#">About the Origin</Card.Link>
  </Card.Body>
</Card>
   
 )
}
export default Meal;


