import classes from './Meal.module.css'
import {  Card ,ListGroupItem ,ListGroup } from 'react-bootstrap'





const Meal=props=>{
 const recipe=props.mealData
//  const ing=recipe[`strIngredient${10}`]
const array=[{
  key:"1",
  recipe:""
}]
 for (let i=1; i<21 ;i++){
   if (recipe[`strIngredient${i}`]){
     array.push({
       key:Math.random(),
       recipe:recipe[`strIngredient${i}`],
      })
     }
   }
 
 
 
 return (
  <Card border='danger' style={{ width: '50rem' , margin:'5rem' }}>
  
  <Card.Body>
    <Card.Title>{recipe.strMeal}</Card.Title>
    <img className={classes.mealIcon} src={recipe.strMealThumb} alt="" />
    <Card.Subtitle className="mb-2 text-muted"><h1>Instructions</h1></Card.Subtitle>
    <Card.Text>
    
      {recipe.strInstructions}
    </Card.Text>
    <Card.Link href="#">Card Link</Card.Link>
    <Card.Link href="#">Another Link</Card.Link>
  </Card.Body>
</Card>




  
 )
}
export default Meal;



