import Button from 'react-bootstrap/Button';
import classes from './Navbar.module.css' 
import { Link } from 'react-router-dom';

import {useState} from 'react';


const SearchMeal=(props )=>{
  const [mealData, setmealData] = useState(null);
  const [recData, setrecData] = useState(null);

  const mealHandler = event=>{
    setrecData(event.target.value)
 
    const getMealData = ()=>{
        fetch(
          'https://www.themealdb.com/api/json/v1/1/search.php?s='+recData
        )
          .then((response) => response.json())
          .then((data) => {
            setmealData(data.meals[0])
          })
          .catch(() => {
            console.log("error");
          })};
  

    return (
      <>
      <div className={classes.search}>
      <input type="text" placeholder="Search your craving" onChange={mealHandler} />
     
      <Button type="submit" onSubmit={getMealData} >Search</Button>

      <Button variant="success">Scan </Button>

      </div>
        <div>
      <h1>{mealData.strMeal}</h1>
      <img  src={mealData.strMealThumb} alt="Biryani.jpg" />
      {array.map(item=>(
        <p key={item.key}>{item.recipe}</p>
      ))}
      <h1>Instructions :-{mealData.strInstructions}</h1>
      
     </div> 
     </>
    )
      }
}
export default SearchMeal
