import {useState} from 'react'
import Button from 'react-bootstrap/Button';

import { Link } from 'react-router-dom';
import classes from './Navbar.module.css'
import logo from '../images/wclogo.jpg'


const NavBar=()=>{
    const [enteredMeal, setenteredMeal] = useState(null);
   


     const mealHandler=(event)=>{
        setenteredMeal(event.target.value)
     }
    

    


   

   
    return (
        <>
        <header>
         <nav className={classes.navbar}>
            
            <img  src={logo} alt="logo" />
           


            
           
            <input type="text" placeholder="Search your cravings"  onChange={mealHandler} />
            <Link to={`/${enteredMeal}`}>
                <Button>Get</Button>
            </Link>

            <Link to='/scan-product'>
            <Button className= "btn" variant="success" >Scan Product</Button>
            </Link>
        
           
      
    
                
            
           
         </nav>
        </header>
       
        </>
    )
}
export default NavBar;
