const Meal=props=>{
  const [mealData, setmealData] = useState(null);
  // const [recCard, setrecCard] = useState(null);
  // const url= "https://www.themealdb.com/api/json/v1/1/search.php?s="+props.name+""
  const nameHandler=event=>{
    const nam=event.target.value
    console.log(event.target.value)
  }

  
  
    fetch(
        'https://www.themealdb.com/api/json/v1/1/search.php?s='+nam
      )
        .then((response) => response.json())
        .then((data) => {
          setmealData(data.meals[0].strInstructions)
        })
        .catch(() => {
          console.log("error");
        });
    
        // fetch(
