        //     setrecCard(data.url)
        //   })
        //   .catch(() => {
        //     console.log("error");
        //   });

    


 
  return (
    <>
    <form action="" onSubmit={nameHandler}>
      <input type="text" />
      <button type="onSubmit">Get recipe</button>
    </form>
    <div>
      <h1>{mealData}</h1>
    
    </div>
    </>
  )
}
export default Meal;
