import Button from 'react-bootstrap/Button';
import classes from './Navbar.module.css' 
import { Link } from 'react-router-dom';
import {useState} from 'react';

const NavBar=()=>{
    const [enteredMeal, setenteredMeal] = useState(null);

    const searchHandler=(event)=>{
        setenteredMeal(event.target.value)
    }
    return (
        <>
        <header>
         <nav>
            <div >

            </div>
            <div className={classes.search}>
            <input type="text" placeholder="Search your craving"  onChange={searchHandler}/>
           
            <Button type="submit" >Search</Button>

            <Button variant="success">Scan </Button>

            </div>
         </nav>
        </header>
        <div>
            {enteredMeal && <Searchmeal recipe={enteredMeal} />}
        </div>
        </>
    )
}
export default NavBar;
