import {useState} from 'react'
import { NavLink } from 'react-router-dom';
import classes from './MainNavigation.module.css'


const NavBar=()=>{
    const [enteredMeal, setenteredMeal] = useState(null);
     const mealHandler=(event)=>{
        setenteredMeal(event.target.value)
     }
    return (
      <header className={classes.header}>
      <div className={classes.logo}>Whats Cooking? </div>
          <nav className={classes.nav}>
              <ul>
              <li>
              <input className={classes.search} type="text" placeholder="Search your cravings"  onChange={mealHandler} />
              </li>
                  <li>
                  <NavLink to={`/${enteredMeal}`} activeClassName={classes.active}>Search</NavLink>
                  </li>
                  <li>
                  <NavLink to='/scan-product' activeClassName={classes.active}>Scan Product</NavLink>
                  </li>
              </ul>
          </nav>
     
  </header>
    )
}
export default NavBar;
