import React from 'react';
import BarcodeScannerComponent from "react-webcam-barcode-scanner";

import { Button } from 'react-bootstrap';
import { Link ,  Redirect} from 'react-router-dom';
import BarCodeScanner from 'barcode-react-scanner';

import { WasmDecoder } from "@impactdk/barcode-scanner";
import { ReactBarcodeScanner } from "@impactdk/react-barcode-scanner";

function Barcode() {

  const [ data, setData ] = React.useState('Not Found');
  const { current: decoder } = useRef(WasmDecoder.getInstance("/public-assets")); 

  const handleBarcode=(barcode)=>{
    setData(barcode)

  }
  

 
 
  return (
    <>

    <ReactBarcodeScanner
    decoder={decoder}
    onFindBarcode={handleBarcode} />
    

    
 
     
   







   
      
      {data!=='Not Found' &&  <Redirect to={`/scan-product/${data}`} />}
      <Link to='/'>
      <Button variant="danger" id="2"  >Cancel</Button>
      </Link>
     
      <p>{data}</p>
    </>
  )
}

export default Barcode;
