import {useState} from 'react'
import Button from 'react-bootstrap/Button';
import Barcode from './Barcode';
import SearchMeal from './SearchMeal';
import { Link } from 'react-router-dom';
import classes from './Navbar.module.css'


const NavBar=()=>{
    const [enteredMeal, setenteredMeal] = useState(null);
    const [barcode, setbarcode] = useState(false);


     const mealHandler=(event)=>{
        setenteredMeal(event.target.value)
     }
     const valueHandler=()=>{
         setenteredMeal('')
     }

     const barcodeHandler=()=>{
        setbarcode(!barcode)
      }


   

   
    return (
        <>
        <header>
         <nav>
            <div >

            </div>
            <div className={classes.search}>
            <input type="text" placeholder="Search your cravings" onChange={mealHandler} />
            <Link to={`/${enteredMeal}`}>
                <Button onclick={valueHandler}>Get</Button>
            </Link>

            <Link to='/scan-product'>
            <Button className= "btn" variant="success" >Scan Product</Button>
            </Link>
           
      
    
                
            </div>
           
         </nav>
        </header>
       
        </>
    )
}
export default NavBar;
