import React , { useState } from 'react';





const Meal=()=>{
  const [mealData, setmealData] = useState(null);

  const getMealData=()=>{
  
    fetch(
        'https://api.spoonacular.com/recipes/findByNutrients?minCarbs=10&maxCarbs=50&number=2&apiKey=a9b075e6656c45f587b86cba1c8c3789'
      )
        .then((response) => response.json())
        .then((data) => {
          setmealData(data)
        })
        .catch(() => {
          console.log("error");
        });
}

 
  return (
    <div>
      <h2>{mealData}</h2>
    </div>
  )
}
export default Meal;
