import Meal from './components/Meal'
import {useState } from 'react'
import Card from './components/UI/Card';
import Barcode from './components/Barcode';

function App() {
  const [mealData, setmealData] = useState(null);
  const [recData, setrecData] = useState(null);
  const [barcode, setbarcode] = useState(false);
  // const [recCard, setrecCard] = useState(null);
  // const url= "https://www.themealdb.com/api/json/v1/1/search.php?s="+props.name+""
  const mealHandler = event=>{
    setrecData(event.target.value)

  }
  const barcodeHandler=()=>{
    setbarcode(true)
  }


    const getMealData = ()=>{
      fetch(
        'https://www.themealdb.com/api/json/v1/1/search.php?s='+recData
      )
        .then((response) => response.json())
        .then((data) => {
          setmealData(data.meals[0])
        })
        .catch(() => {
          console.log("error");
        })};


  return (
    <div>

      <input type="text" onChange={mealHandler} />
      <button type="submit" onClick={getMealData}>Get Recipe</button>
      <button onClick={barcodeHandler}>Scan Product</button>
      {barcode && <Barcode />}

    <Card>
      {mealData && <Meal mealData={mealData}/>}
    </Card>

    </div>
  );
}

export default App;
