import classes from './Meal.module.css'
import { Button, Card ,ListGroupItem ,ListGroup } from 'react-bootstrap'
const Meal=props=>{
 const recipe=props.mealData
//  const ing=recipe[`strIngredient${10}`]
const array=[{
  key:"1",
  recipe:""
}]
 for (let i=1; i<21 ;i++){
   if (recipe[`strIngredient${i}`]){
     array.push({
       key:Math.random(),
       recipe:recipe[`strIngredient${i}`],
      })
     }
   }
 
 
 
 return (
  <Card bg="success" style={{ width: '18rem' }}>
   <Card.Title>{recipe.strMeal}</Card.Title>
   <br />
  <Card.Img className={classes.img} variant="top" src={recipe.strMealThumb} />
  <br />
  <Card.Body>
  <h1>Ingredients:-</h1>
  <br />
   
    <ListGroup className="list-group-flush">
  {array.map(item=>(
       <ListGroupItem  key={item.key}>{item.recipe}</ListGroupItem>
     ))}
   
  </ListGroup>
  <br />
  <h1>Instructions-</h1>
    <Card.Text>
    {recipe.strInstructions}
    </Card.Text>
  </Card.Body>
 
  <Card.Body>
    <Card.Link href="#">Watch on YouTube</Card.Link>
    <Card.Link href="#">Learn More.</Card.Link>
  </Card.Body>
</Card>
 )
}
export default Meal;



