  //       setmealData(data.meals[0])
  //     })
  //     .catch(() => {
  //       console.log("error");
  //     });

  // }





  return (
    <>
      {/* <button onClick={getData}>click</button> */}
      {mealData && <Meal mealData={mealData} />}
    </>


  )

}
export default SearchMeal
