import {useState} from 'react'
import Button from 'react-bootstrap/Button';

import { Link } from 'react-router-dom';
import classes from './Navbar.module.css'
import logo from '../images/wclogo3.jpg'
import icon from '../images/resize.png'
import icon2 from '../images/magnifier.png'


const NavBar=()=>{
    const [enteredMeal, setenteredMeal] = useState(null);
   


     const mealHandler=(event)=>{
        setenteredMeal(event.target.value)
     }
    

    


   

   
    return (
        <>
        <header>
         <nav className={classes.navbar}>
            
            <img  src={logo} alt="logo" />
           


            
           
            <input className={classes.search} type="text" placeholder="Search your cravings"  onChange={mealHandler} />
            <Link to={`/${enteredMeal}`}>
                <img src={icon2} alt="magnifier" />
            </Link>

            <Link to='/scan-product'>
            <img src={icon} className={classes.icon1} alt="icon" />
            </Link>
        
           
      
    
                
            
           
         </nav>
        </header>
       
        </>
    )
}
export default NavBar;
