import classes from './Meal.module.css'
import { Button, Card } from 'react-bootstrap'
const Meal=props=>{
 const recipe=props.mealData
//  const ing=recipe[`strIngredient${10}`]
const array=[{
  key:"1",
  recipe:""
}]
 for (let i=1; i<21 ;i++){
   if (recipe[`strIngredient${i}`]){
     array.push({
       key:Math.random(),
       recipe:recipe[`strIngredient${i}`],
      })
     }
   }
 
 
 
 return (
  <Card className="text-center">
  <Card.Header>{recipe.strMeal}</Card.Header>
  <Card.Body>
    <Card.Title>{recipe.strMeal}</Card.Title>
    <img className={classes.img} src={recipe.strMealThumb} alt="Biryani.jpg" />
     {array.map(item=>(
       <p key={item.key}>{item.recipe}</p>
     ))}
    <Card.Text>
    {recipe.strInstructions}
    </Card.Text>
    <Button variant="primary">Go somewhere</Button>
  </Card.Body>
  <Card.Footer className="text-muted">2 days ago</Card.Footer>
</Card>

  
 )
}
export default Meal;



